import {
  ADD_TO_CART,
  ADD_QUANTITY,
  ADD_SHIPPING,
  REMOVE_ITEM,
  SUB_QUANTITY,
} from "./Types";

export function addToCart(id) {
  return {
    type: ADD_TO_CART,
    id,
  };
}
export function removeItem(id) {
  return {
    type: REMOVE_ITEM,
    id,
  };
}
export function addQuantity(id) {
  return {
    type: ADD_QUANTITY,
    id,
  };
}
export function addShipping(id) {
  return {
    type: ADD_SHIPPING,
    id,
  };
}
export function subQuantity(id) {
  return {
    type: SUB_QUANTITY,
    id,
  };
}
